INSERT INTO `wp_options` VALUES (215,'_transient_dash_88ae138922fe95674369b1cb3d215a2b','<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2016/04/coleman/\'>WordPress 4.5 “Coleman”</a> <span class=\"rss-date\">April 12, 2016</span><div class=\"rssSummary\">Version 4.5 of WordPress, named “Coleman” in honor of jazz saxophonist Coleman Hawkins, is available for download or update in your WordPress dashboard. New features in 4.5 help streamline your workflow, whether you’re writing or building your site. Editing Improvements Inline Linking Stay focused on your writing with a less distracting interface that keeps you in [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'http://wptavern.com/linus-torvalds-explains-how-open-source-led-to-the-success-of-linux\'>WPTavern: Linus Torvalds Explains How Open Source Led to the Success of Linux</a></li><li><a class=\'rsswidget\' href=\'http://wptavern.com/wordpress-4-6-development-kicks-off-this-week-dominik-schilling-to-lead-release\'>WPTavern: WordPress 4.6 Development Kicks Off This Week, Dominik Schilling to Lead Release</a></li><li><a class=\'rsswidget\' href=\'http://wptavern.com/sucuri-partners-with-lets-encrypt-to-offer-free-ssl-certificates-to-all-customers\'>WPTavern: Sucuri Partners with Let’s Encrypt to Offer Free SSL Certificates to All Customers</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\'dashboard-news-plugin\'><span>Popular Plugin:</span> <a href=\'https://wordpress.org/plugins/siteorigin-panels/\' class=\'dashboard-news-plugin-link\'>Page Builder by SiteOrigin</a>&nbsp;<span>(<a href=\'plugin-install.php?tab=plugin-information&amp;plugin=siteorigin-panels&amp;_wpnonce=a0dfd83e49&amp;TB_iframe=true&amp;width=600&amp;height=800\' class=\'thickbox\' title=\'Page Builder by SiteOrigin\'>Install</a>)</span></li></ul></div>','no');
