INSERT INTO `wp_posts` VALUES (207,1,'2016-04-19 19:40:19','2016-04-19 19:40:19','[et_pb_section background_color=\"#132c47\" inner_shadow=\"off\" parallax=\"on\"][et_pb_row][et_pb_column type=\"4_4\"][et_pb_divider admin_label=\"Divider\" color=\"#ffffff\" show_divider=\"off\" height=\"60\" /][et_pb_text admin_label=\"Text\" background_layout=\"dark\" text_orientation=\"center\"]<h1 style=\"font-size: 52px;\">Product Features</h1>[/et_pb_text][/et_pb_column][/et_pb_row][et_pb_row][et_pb_column type=\"4_4\"][et_pb_image admin_label=\"Image\" src=\"http://elegantthemesimages.com/images/premade/d2-placeholder-1920.jpg\" url_new_window=\"off\" animation=\"bottom\" show_in_lightbox=\"off\" /][/et_pb_column][/et_pb_row][et_pb_row][et_pb_column type=\"1_4\"][et_pb_blurb admin_label=\"Blurb\" title=\"Advanced Page Builder\" url_new_window=\"off\" animation=\"top\" background_layout=\"dark\" text_orientation=\"center\" use_icon=\"on\" use_circle=\"on\" circle_color=\"#0d2035\" use_circle_border=\"off\" circle_border_color=\"#2caaca\" icon_placement=\"top\" font_icon=\"\" icon_color=\"#2ea3f2\"]Divi will change the way you build websites forever. The advanced page builder makes it possible to build truly dynamic pages without learning code.[/et_pb_blurb][/et_pb_column][et_pb_column type=\"1_4\"][et_pb_blurb admin_label=\"Blurb\" title=\"Key Elements\" url_new_window=\"off\" animation=\"top\" background_layout=\"dark\" text_orientation=\"center\" use_icon=\"on\" use_circle=\"on\" circle_color=\"#0d2035\" use_circle_border=\"off\" circle_border_color=\"#2caaca\" icon_placement=\"top\" font_icon=\"\" icon_color=\"#2ad4e0\"]The builder comes packed with tons of great modules, and more are on the way! Combine and arrange them in any order. The possibilities are countless.[/et_pb_blurb][/et_pb_column][et_pb_column type=\"1_4\"][et_pb_blurb admin_label=\"Blurb\" title=\"Target Audience\" url_new_window=\"off\" animation=\"top\" background_layout=\"dark\" text_orientation=\"center\"